import React, { useState } from "react";
import axios from "axios";

const SPREADSHEET_ID = "14NnMh48ape9Yb6U3qYpJ6cVMHvmcYNnfdFDI09SX1ag";
const API_KEY = "-----BEGIN PRIVATE KEY-----\nMIIEvgIBADANBgkqhkiG9w0BAQEFAASCBKgwggSkAgEAAoIBAQDjcJZbd1xOHCEO\nLfIJ/+yKbVBDKri5Syj0mtCXDXLc15R2u2klJZILQGnCeXgI1eIRY2t8wtpJyds7\nDsaRuXGXzIbtwwoDf3Zm8AXjgR10EgG1RD6J/KEHmoSm7NMDVkzeVf7lUE2MR92y\nGaXTKLZF3DQetFLTom0et/IbcRJdJpXB0dexPQ5zAVP8KnLB8ASwDHjVCmpadAqS\n9j6LeSTSbIyy9dTcbXTtBeACt3EOyLCxG2/IFxsn67olUeW9pSgASwpe0d4L421+\nqaVx/p+v8amIZtu8n/6MkRq2DXgHfGrnG++AAUsCTHjwIa44dhKshx7gMqecAKXf\nySK/bBtdAgMBAAECggEAM2P98WJlvrd/55SNn2FOuROMonSdgy6+rRBrJ+pvxQNI\nJlDf5/cWigjOrNTmALcVSwEIKUtc5/XenbhPuV8p0dcyuLgMg/BR2F4NMpP6y/6C\nmMr4B5VH7HAnX5Fc96DRIx7Sxs1PjG4zo3nEnJMTJnNkaniry2qk1d9R6w3wTJAN\ncaIDTu7k1E1xexu3QZCy2HgYMYTW8ze/mNd4Too+FbyRkiCmV1YnByqwM0j6qvX1\nY0dWLYvRE6pG3lShHo6XsRY3IMuyHF65RtLdSBjaIDrSL6/BuoSvv/NY1l6TNlt1\nNOwNIusd/d0yumjOHxbeNeK5hOsdDHF3gPEeQyjgAQKBgQD9qgA6uARmyziGG49l\n94Kv4M/goPSUsVZG4YijdGoxNBGAIWzwAMc/nbWvVO7+O+M33KOIm8vmVcxiMekp\nDGbnqoKgon1sdOGjmA3goCY/5Y8lOYK5i5T2i6bBJH2zGctI00tg7+Ak1eqSVEaw\nSgx/4STyUG9RsCK/83bOWpd7XQKBgQDliMOfyKBjqN5px+pdID5qA6ZMEQ9W3P9X\nYmNHOAQ32VjerwPZQzYKIxMfsJoj2J7oCHYhvKv4zXu05WSodmsR0LIGr5H+ZHTW\n/+2aO3Lq7cuSMcD3tOtNw/u/zvObwB1ImuMpZoSdf1jRofhqPGoc4BG9lLoDilRc\n3SL4jX0gAQKBgCMq9IkvhfyyZA3DuGfMnuO/aVKdPgt3YpzFG+p9c1OLAnofKDlG\nAlnBhbTfM5svtlrCTjHcV7KBZqqefNLiE0o/C4k1venRco75v8LKsULekq/4fzU2\nMtrTlbTlWEACbXqVQLgZyZuRxMmwwXQjcAQytKqbmDNdFhRCrtABI0tJAoGBAJFp\nOPUKtoEsQ+qjmZdxi9jBgyKBVERlMOX9EIPXuuReruwz165V4/A8FCtpR90ZqGG6\nqovo6NFv68TH5iLLEj1xBrcxlr2gilyCOdYRf1XRW7u8BVFnrgHP/Lagtree125d\noTdjGPRl7rCveZ5js52jXdnKVKVVhuhlt3fausABAoGBAMK572Egimztl32nz0Ev\nv73zjm6tYfuPlcW0+xNq6h/56d4Vu2Xd/k37WXPBdj4w+HpPED57i2uTDglC4Hrd\n8a3iAZpOTOPbvqH8NWtE6ryKeQkbLXFmsRiIZ80I6u2y8qdyJugNp4+eoK3itkFi\nhWqR8vMFRXGwoYMOlqp/AIQO\n-----END PRIVATE KEY-----\n";

const sendDataToSheet = (data) => {
  axios
    .post(
      `https://sheets.googleapis.com/v4/spreadsheets/${SPREADSHEET_ID}/values/A1:append?valueInputOption=RAW&key=${API_KEY}`,
      {
        values: [[data.name, data.email, data.message]],
      }
    )
    .then((response) => {
      console.log("Data sent successfully: ", response);
    })
    .catch((error) => {
      console.error("Error sending data: ", error);
    });
};

const Sheet = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleInputChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Submitting form data: ", formData);
    sendDataToSheet(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={formData.name}
        onChange={handleInputChange}
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleInputChange}
      />
      <textarea
        name="message"
        placeholder="Message"
        value={formData.message}
        onChange={handleInputChange}
      />
      <button type="submit">Submit</button>
    </form>
  );
};

export default Sheet;
