import axios from "axios";

const SPREADSHEET_ID = "your-spreadsheet-id";
const API_KEY = "your-api-key";

const sendDataToSheet = (data) => {
  axios
    .post(
      `https://sheets.googleapis.com/v4/spreadsheets/${SPREADSHEET_ID}/values/A1:append?valueInputOption=RAW&key=${API_KEY}`,
      {
        values: [[data.name, data.email, data.message]],
      }
    )
    .then((response) => {
      console.log("Data sent successfully: ", response);
    })
    .catch((error) => {
      console.error("Error sending data: ", error);
    });
};

import React, { useState } from "react";

const handleSubmit = (event) => {
  event.preventDefault();
  console.log("Submitting form data: ", formData);
  sendDataToSheet(formData);
};


const Sheet = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleInputChange = (event) => {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("Submitting form data: ", formData);
    // Send formData to Google Sheets API
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="name"
        placeholder="Name"
        value={formData.name}
        onChange={handleInputChange}
      />
      <input
        type="email"
        name="email"
        placeholder="Email"
        value={formData.email}
        onChange={handleInputChange}
      />
      <textarea
        name="message"
        placeholder="Message"
        value={formData.message}
        onChange={handleInputChange}
      />
      <button type="submit">Submit</button>
    </form>
  );
};

export default Sheet;
