import React, { useState, useEffect } from 'react';
import Display from '../components/scouting/Display';
import TestForm from '../components/scouting/TestForm.js';
import classes from './/Timer.css';

//let testButton = 0;
var testButton = '0';
function TimerPage() {
  //const Timer = () => {
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);
  var gameMode = 'Unknown';

  function toggle() {
    setIsActive(!isActive);
  }

  function test() {
    testButton = '1';
  }

  function reset() {
    setSeconds(150);
    setIsActive(false);
  }

  useEffect(() => {
    let interval = null;
    if (isActive) {
      interval = setInterval(() => {
        if (seconds > 0) {
          setSeconds(seconds => seconds - 1);
        }
      }, 1000);
    } else if (!isActive && seconds !== 0) {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isActive, seconds]);

  if (seconds > 135) {
    gameMode = 'Auto';
  } else if ( 30 < seconds) {
    gameMode = 'Teleop';
  } else if (seconds > 0) {
    gameMode = 'EndGame';
  } else if (seconds < 1) {
    gameMode = 'GameOver'
  }

  return (
    <div className="app">
      <div className="time">
        {seconds}s
      </div>
      <p> </p>
      <div className="row">
        <button className={`button button-primary button-primary-${isActive ? 'active' : 'inactive'}`} onClick={toggle}>
          {isActive ? 'Pause' : 'Start'}
        </button>
        <button className="button" onClick={reset}>
          Reset
        </button>
      </div>
      <Display />
      {gameMode}
      <div className="count">
      <p> </p>
      <p>Counter:</p>
      <button type="button" onClick={test}>Plus One</button>
      <p> </p>
      {testButton}
      </div>
    </div>
  );
};



export default TimerPage;
